import React, { useState } from 'react';
import s from './PageLayout.module.css';
import SideBar from '../SideBar/SideBar';
import Header from '../Header/Header';

const PageLayout = (props) => {
	let [sideBarStatus, setSideBarStatus] = useState(false);

	return (
		<div className="page-container">
			<div className="page-container__left-column" onMouseLeave={() => setSideBarStatus(false)} onMouseEnter={() => setSideBarStatus(true)}>
				<SideBar status={sideBarStatus} />
			</div>
			<div className="page-container__right-column">
				<Header />
				<div className="overflow-container">
					<div className="content-container">{props.children}</div>
				</div>
			</div>
		</div>
	);
};

export default PageLayout;
