import { useEffect, useState } from 'react';
import s from './NewsFilter.module.css';

const NewsFilter = ({ tags }) => {
	let [activeButton, setActiveButton] = useState(0);

	useEffect(() => {
		console.log(tags);
	}, [tags]);

	return (
		<div className={s.filters}>
			<div className={`${s.filters_row} ${s.params}`}>
				<button onClick={() => setActiveButton(0)} className={activeButton === 0 && s.active}>
					Усі
				</button>
				<button onClick={() => setActiveButton(1)} className={activeButton === 1 && s.active}>
					Сьогодні
				</button>
				<button onClick={() => setActiveButton(2)} className={activeButton === 2 && s.active}>
					Популярне
				</button>
			</div>
			<div className={`${s.filters_row} ${s.tags}`}>
				{tags.map((el, index) => {
					return (
						<button lassName={activeButton === index + 3 && s.active} onClick={() => setActiveButton(index + 3)}>
							{el}
						</button>
					);
				})}
			</div>
		</div>
	);
};

export default NewsFilter;
