const MasonryGallery = () => {
	let [isContainerHeight, setContainerHeight] = useState(0);
	let [isLoadImages, setIsLoadImages] = useState(false);
	let ref = useRef();

	useEffect(() => {
		let loadedStatusImages = [];

		let currentImg = document.querySelectorAll(`.${s.item} img`);
		currentImg.forEach((item, index) => {
			loadedStatusImages.push(false);
			currentImg.onload = function () {
				loadedStatusImages[index] = true;
			};
		});

		let interval = setInterval(() => {
			loadedStatusImages.includes(false);

			if (!loadedStatusImages) {
				setIsLoadImages(true);
			}
		}, 50);

		return () => {
			clearInterval(interval);
		};
	}, []);

	useEffect(() => {
		const observer = new ResizeObserver((entries) => {
			let galleryItems = document.querySelectorAll(`.${s.item}`);
			let slicedItems = [];

			let concatHeight1 = 0;
			let concatHeight2 = 0;
			let concatHeight3 = 0;
			let concatHeight4 = 0;

			galleryItems.forEach((item, index) => {
				if (index < galleryItems.length - 3) {
					slicedItems.push(item);
				}
			});

			for (let index = 0; index < slicedItems.length; index += 4) {
				const element = slicedItems[index];
				concatHeight1 += element.offsetHeight * 1;
			}
			for (let index = 1; index < slicedItems.length; index += 4) {
				const element = slicedItems[index];
				concatHeight2 += element.offsetHeight * 1;
			}
			for (let index = 2; index < slicedItems.length; index += 4) {
				const element = slicedItems[index];
				concatHeight3 += element.offsetHeight * 1;
			}
			for (let index = 3; index < slicedItems.length; index += 4) {
				const element = slicedItems[index];
				concatHeight4 += element.offsetHeight * 1;
			}
			concatHeight1 = concatHeight1 + (slicedItems.length / 4) * 8;
			concatHeight2 = concatHeight2 + (slicedItems.length / 4) * 8;
			concatHeight3 = concatHeight3 + (slicedItems.length / 4) * 8;
			concatHeight4 = concatHeight4 + (slicedItems.length / 4) * 8;

			setContainerHeight(Math.max(concatHeight1, concatHeight2, concatHeight3, concatHeight4));
			console.log(concatHeight1);
			console.log(concatHeight2);
			console.log(concatHeight3);
			console.log(concatHeight4);
			console.log(Math.max(concatHeight1, concatHeight2, concatHeight3, concatHeight4));

			console.log(entries);
			console.log(entries[0].contentRect);
		});

		if (isLoadImages) {
			observer.observe(ref.current);
		}
		return () => {
			observer.unobserve(ref.current);
		};
	}, [isLoadImages]);

	return (
		<div ref={ref} className={s.list} style={{ height: isContainerHeight }}>
			{date2.map((galleryItem) => (
				<div className={s.item}>
					<img src={`${galleryItem}`} />
				</div>
			))}

			<div className={`${s.item} ${s.break}`}></div>
			<div className={`${s.item} ${s.break}`}></div>
			<div className={`${s.item} ${s.break}`}></div>
		</div>
	);
};

export default MasonryGallery;
