import s from './LoginPage.module.css';
import logoImg from '../../assets/img/login_logo.png';
import { SignInButton } from '../../components/SignInButton/SignInButton';
import AlertModalWindow from '../../components/AlertModalWindow/AlertModalWindow';

const LoginPage = () => {
	let data = {
		acceptButton: {
			text: 'Видалити',
			handler: () => {
				alert('Видалити');
			},
		},
		cencelButton: {
			text: 'Повернутися',
			path: '/admin-news-feed/',
		},
		isFetching: false,
		fetchingResult: false,
		text: {
			question: 'Видалити цю подію?',
			accept: 'Подію подалено',
			error: 'Відбулась помилка',
		},
	};

	return (
		<div className={s.login_page}>
			<div className={s.login_page__container}>
				<img src={logoImg} alt="logo" />
				<h2>КОРПОРТАЛ</h2>
				<p>Увійдіть, будь ласка, до системи, щоб отримати доступ до порталу</p>
				<SignInButton />
			</div>
			{/* <AlertModalWindow data={data} /> */}
		</div>
	);
};

export default LoginPage;
