import BirthdaySection from './BirthdaySection/BirthdaySection';
import EventSection from './EventSection/EventSection';
import NewsSection from './NewsSection/NewsSection';
import HeroSection from './HeroSection/HeroSection';
import TodaySection from './TodaySection/TodaySection';
import VivoChatSection from './VivoChatSection/VivoChatSection';
import { useEffect, useRef, useState } from 'react';

const HomePage = () => {
	let ref = useRef();

	const [width, setwidth] = useState(0);
	const [scroll, setScroll] = useState(0);

	const handleScroll = () => {
		setScroll(window.scrollY);
	};

	useEffect(() => {
		const observer = new ResizeObserver((entries) => {
			setwidth(entries[0].contentRect.width / 2 - 15);
		});
		observer.observe(ref.current);
		window.addEventListener('scroll', handleScroll);

		return () => {
			ref.current && observer.unobserve(ref.current);
			window.removeEventListener('scroll', handleScroll);
		};
	}, []);

	useEffect(() => {
		console.log(width);
		console.log(ref.current.getBoundingClientRect());
	}, [width]);

	return (
		<div>
			<h1>Головна</h1>
			<div className="row no-wrap">
				<HeroSection />
				<TodaySection />
			</div>
			<div className="row">
				<EventSection />
			</div>
			<div ref={ref} className="row mobile-revers">
				<VivoChatSection />
				<div style={{ width: width }} className={`column-50 sticky-position`}>
					<NewsSection />
					<BirthdaySection />
				</div>
			</div>
		</div>
	);
};

export default HomePage;
