import { useEffect, useState } from 'react';
import s from './NewsFilter.module.css';

const NewsFilter = ({ tags, setFilterParams, todayPosts }) => {
	let [activeButton, setActiveButton] = useState(0);

	return (
		<div className={s.filters}>
			<div className={`${s.filters_row} ${s.params}`}>
				<button
					onClick={(e) => {
						setActiveButton(0);
						setFilterParams({ params: e.currentTarget.innerHTML, tags: false });
					}}
					className={activeButton === 0 && s.active}>
					Усі
				</button>
				<button
					onClick={(e) => {
						setActiveButton(1);
						setFilterParams({ params: 'Сьогодні', tags: false });
					}}
					className={activeButton === 1 && s.active}>
					Сьогодні ({todayPosts})
				</button>
				<button
					onClick={(e) => {
						setActiveButton(2);
						setFilterParams({ params: e.currentTarget.innerHTML, tags: false });
					}}
					className={activeButton === 2 && s.active}>
					Популярне
				</button>
			</div>
			<div className={`${s.filters_row} ${s.tags}`}>
				{tags.map((el, index) => {
					return (
						<button
							className={activeButton === index + 3 && s.active}
							onClick={(e) => {
								setActiveButton(index + 3);
								setFilterParams({ params: e.currentTarget.innerHTML, tags: true });
							}}>
							{el}
						</button>
					);
				})}
			</div>
		</div>
	);
};

export default NewsFilter;
