import s from './CommentsList.module.css';

const CommentsList = ({ comment }) => {
	let { autor, comment_txt, post_date } = comment;
	return (
		<div className={s.commets_container}>
			<p className={s.commets_h}>Коментарі:</p>
			<ul className={s.commets_list}>
				<li className={s.commets_item}>
					<p className={s.commets_title}>{autor}</p>
					<p className={s.commets_date}>{post_date}</p>
					<p className={s.commets_text}>{comment_txt}</p>
				</li>
			</ul>
		</div>
	);
};

export default CommentsList;
