import { useEffect, useReducer, useState } from 'react';
import s from './Header.module.css';
import { useMsal } from '@azure/msal-react';
import { callMe, callPhoto, getAdminGroup, getAdminMembers, getGroups, getStructureCompany } from '../../api/graph';
import { loginRequest } from '../../authConfig';
import settingIcon from '../../assets/img/icons/settings-icon.svg';
import bellIcon from '../../assets/img/icons/bell-icon.svg';
import searchIcon from '../../assets/img/icons/search-icon.svg';
import logo from '../../assets/img/Frame 40.png';
import { NavLink } from 'react-router-dom';
import { sendUserProfile } from '../../api/api';
import { useDispatch } from 'react-redux';
import { setAdmin, setUser } from '../../store/userSlice';
import { fetchSeenNews } from '../../store/thunks';

const Header = ({ toggleMobileMode, mobileMode }) => {
	const dispatch = useDispatch();
	const { instance, accounts } = useMsal();
	let [urlImg, setUrlImg] = useState(null);
	let [graphData, setGraphData] = useState(null);

	function RequestProfilePhoto() {
		instance
			.acquireTokenSilent({
				...loginRequest,
				account: accounts[0],
			})
			.then((response) => {
				callPhoto(response.accessToken)
					.then((response) => response.blob())
					.then((result) => {
						const url = window.URL || window.webkitURL;
						const blobUrl = url.createObjectURL(result);
						setUrlImg(blobUrl);
					});
			});
	}

	function RequestProfileData() {
		instance
			.acquireTokenSilent({
				...loginRequest,
				account: accounts[0],
			})
			.then((response) => {
				callMe(response.accessToken).then((response) => {
					setGraphData(response);
				});
			});
	}

	function RequestAdminsGroupData() {
		instance
			.acquireTokenSilent({
				...loginRequest,
				account: accounts[0],
			})
			.then((response) => {
				getAdminMembers(response.accessToken)
					.then((response) => {
						return response.json();
					})
					.then((result) => {
						let adminStatus = result.value.find(({ id }) => id === graphData.id);
						if (adminStatus) {
							dispatch(setAdmin('isAdmin'));
						} else {
							dispatch(setAdmin('notAdmin'));
						}
						return result;
					});
			});
	}

	// function RequestAdminsGroupName() {
	// 	instance
	// 		.acquireTokenSilent({
	// 			...loginRequest,
	// 			account: accounts[0],
	// 		})
	// 		.then((response) => {
	// 			getAdminGroup(response.accessToken)
	// 				.then((response) => {
	// 					console.log(response);
	// 					return response.json();
	// 				})
	// 				.then((result) => {
	// 					console.log(result);
	// 				});
	// 		});
	// }

	useEffect(() => {
		RequestProfileData();
		RequestProfilePhoto();
	}, []);

	useEffect(() => {
		if (accounts.length > 0 && graphData) {
			dispatch(setUser(graphData));
			dispatch(fetchSeenNews(graphData.id));
			sendUserProfile(graphData);
			RequestAdminsGroupData();
			let cookieValue = document.cookie.replace(/(?:(?:^|.*;\s*)test2\s*\=\s*([^;]*).*$)|^.*$/, '$1');
			console.log(cookieValue);

			console.log(
				'_fbp=fb.1.1713206240356.1559674917; sbjs_migrations=1418474375998%3D1; sbjs_first=typ%3Dreferral%7C%7C%7Csrc%3Dstatics.teams.cdn.office.net%7C%7C%7Cmdm%3Dreferral%7C%7C%7Ccmp%3D%28none%29%7C%7C%7Ccnt%3D%2F%7C%7C%7Ctrm%3D%28none%29; _gcl_au=1.1.1023973167.1728890827; sbjs_first_add=fd%3D2024-11-20%2015%3A40%3A25%7C%7C%7Cep%3Dhttps%3A%2F%2Fwww.softcom.ua%2Fua%2Fbas%2Flearn%2Fcurs_bas_byh_dlya_ukr.php%7C%7C%7Crf%3Dhttps%3A%2F%2Fwww.google.com%2F; sbjs_current=typ%3Dreferral%7C%7C%7Csrc%3Dstatics.teams.cdn.office.net%7C%7C%7Cmdm%3Dreferral%7C%7C%7Ccmp%3D%28none%29%7C%7C%7Ccnt%3D%2F%7C%7C%7Ctrm%3D%28none%29; _gid=GA1.2.74534637.1733927956; sbjs_current_add=fd%3D2024-12-12%2012%3A35%3A20%7C%7C%7Cep%3Dhttps%3A%2F%2Fwww.softcom.ua%2Fua%2Fbas%2Fturnkey-solutions%2F%7C%7C%7Crf%3Dhttps%3A%2F%2Fstatics.teams.cdn.office.net%2F; sbjs_udata=vst%3D314%7C%7C%7Cuip%3D%28none%29%7C%7C%7Cuag%3DMozilla%2F5.0%20%28Windows%20NT%2010.0%3B%20Win64%3B%20x64%29%20AppleWebKit%2F537.36%20%28KHTML%2C%20like%20Gecko%29%20Chrome%2F131.0.0.0%20Safari%2F537.36; _ga=GA1.1.1763079884.1713206240; _ga_6HN2ZXF9E8=GS1.1.1734087144.1.0.1734087148.56.0.0'
			);
		}
	}, [graphData]);

	return (
		<header>
			<div className={`content-container ${s.header} `}>
				<div className={s.header_column}>
					<NavLink to="/" className={s.header_logo}>
						<img src={logo} alt="" />
					</NavLink>

					{/* Панель ПОШУКУ */}
					{/* <div className={s.header_search}>
						<button>
							<img src={searchIcon} alt="bell icon" />
						</button>
						<input type="text" placeholder="Пошук тут..." />
					</div> */}
				</div>
				<div className={s.header_column}>
					{/* КНОПКИ НАЛАШТУВАННЯ ТА ОПОВІЩЕННЯ */}
					{/* <div className={s.header_btns}>
						<a href="">
							<img src={settingIcon} alt="setting icon" />
						</a>
						<a href="" className={s.active}>
							<img src={bellIcon} alt="bell icon" />
						</a>
					</div> */}
					<div className={s.header_profile}>
						{urlImg && <img src={`${urlImg}`} alt="" />}
						{graphData && (
							<div>
								<p>{graphData.displayName}</p>
								<p>{graphData.jobTitle}</p>
							</div>
						)}
					</div>
					<div className={`${s.header_mobile__button}`}>
						<button className={`${mobileMode && s.visible}`} onClick={(e) => toggleMobileMode((button) => !button)}>
							<span></span>
						</button>
					</div>
				</div>
			</div>
		</header>
	);
};

export default Header;
