import { useEffect, useState } from 'react';
import s from './FilesList.module.css';

const FilesList = ({ data }) => {
	let [isFiles, setFiles] = useState([]);

	useEffect(() => {
		data && setFiles(data);
	}, [data]);

	return (
		<div className={s.files}>
			{isFiles.length > 0 &&
				isFiles.map(
					(fileItem) =>
						fileItem && (
							<div className={s.files__item}>
								<p>hi world</p>
							</div>
						)
				)}
		</div>
	);
};

export default FilesList;
