import { memo, useEffect, useState } from 'react';
import s from './NewsList.module.css';
import NewsItem from '../NewsItem/NewsItem';

const NewsList = ({ data, setTags, filterParams }) => {
	let [news, setNews] = useState([]);
	let [visibleNews, setVisibleNews] = useState([]);

	const getUniqueTags = (arr) => {
		if (arr) {
			let concatedArray = [];

			arr.forEach((element, index) => {
				if (index !== arr.length - 1) {
					concatedArray = [...concatedArray, ...element.tags];
				}
			});

			let uniqueTags = [...new Set(concatedArray)];

			return uniqueTags;
		}
	};
	const getFilteredNews = () => {
		if (filterParams.tags) {
			alert(true);
		} else {
			alert(false);
		}
	};

	useEffect(() => {
		setNews(data);
		setTags(getUniqueTags(data));
	}, [data]);

	useEffect(() => {
		console.log(news);
		getFilteredNews();
		setVisibleNews(news);
	}, [news]);
	useEffect(() => {
		console.log(visibleNews);
	}, [visibleNews]);

	return (
		<div className={s.news_container}>
			<p>{filterParams.params}</p>
			<div className={s.news}>
				{visibleNews.length > 0 ? (
					news.map((item, index) => {
						if (index < news.length - 1 && news.length > 0) {
							return <NewsItem item={item} key={index} />;
						}
					})
				) : (
					<p>Нічого не знайдено</p>
				)}
			</div>
		</div>
	);
};

export default memo(NewsList);
