import BirthdaySection from './BirthdaySection/BirthdaySection';
import EventSection from './EventSection/EventSection';
import NewsSection from './NewsSection/NewsSection';
import HeroSection from './HeroSection/HeroSection';
import TodaySection from './TodaySection/TodaySection';
import VivoChatSection from './VivoChatSection/VivoChatSection';
import { useEffect, useRef, useState } from 'react';

const HomePage = () => {
	let ref = useRef();
	let ref2 = useRef();
	let ref3 = useRef();

	const [width, setwidth] = useState(0);
	const [scroll, setScroll] = useState(0);
	const [windowHeight, setWindowHeight] = useState(null);

	const handleScroll = (e) => {
		if (windowHeight < 975) {
			console.log(ref.current.getBoundingClientRect().top);
			console.log(ref3.current.getBoundingClientRect().height);
			console.log(windowHeight);

			setScroll(ref.current.getBoundingClientRect().bottom - 95);
		} else {
			setScroll(ref.current.getBoundingClientRect().top - 95);
		}
	};

	useEffect(() => {
		const observer = new ResizeObserver((entries) => {
			setwidth(entries[0].contentRect.width / 2 - 15);
			setWindowHeight(window.screen.height);
			console.log(window.screen.height);
		});
		observer.observe(ref.current);

		return () => {
			ref.current && observer.unobserve(ref.current);
			window.removeEventListener('scroll', handleScroll);
		};
	}, []);

	useEffect(() => {
		let el = document.querySelector('.main-scroll-block');
		el.addEventListener('scroll', handleScroll);

		return () => {
			window.removeEventListener('scroll', handleScroll);
		};
	}, [windowHeight]);

	return (
		<div>
			<h1>Головна</h1>
			<div className="row no-wrap">
				<HeroSection />
				<TodaySection />
			</div>
			<div className="row">
				<EventSection />
			</div>
			<div ref={ref} className="row mobile-revers">
				<VivoChatSection ref1={ref2} />
				<div
					ref={ref3}
					style={{ width: width }}
					className={`column-50 ${scroll < 0 && 'sticky-position'} ${windowHeight < 975 && 'sticky-position__bottom'}`}>
					<NewsSection />
					<BirthdaySection />
				</div>
			</div>
		</div>
	);
};

export default HomePage;
