import { useState } from 'react';
import s from './ShopFilter.module.css';

const ShopFilter = () => {
	let [isActiveBtn, setActiveBtn] = useState(-1);

	return (
		<div className="row">
			<div className={s.filters}>
				<div className={s.filters_search}>
					<button title={'Пошук'} disabled={isLoading} onClick={searchHandler}>
						<img src={searchIcon} alt="bell icon" />
					</button>
					<input
						disabled={isLoading}
						onChange={(e) => e.target.value === '' && nulledUsersAfterSearch()}
						onKeyDown={(e) => e.key === 'Enter' && searchHandler()}
						ref={ref}
						type="text"
						placeholder="Пошук тут..."
					/>
				</div>

				<button
					className={`base_btn white ${isActiveBtn === 0 && 'active'}`}
					onClick={() => {
						setActiveBtn(0);
					}}>
					Немає в наявності
				</button>
			</div>
		</div>
	);
};

export default ShopFilter;
