		title: 'title',
		text: 'text',
	},
];
const newsList2 = [
	{
		img: 'https://portal.softcom.ua/content/pictures/news/115/0_9a04a7a25c3ffa24f9710eed855303b4.webp',
		title: 'title2',
		text: 'text',
	},
	{
		img: 'https://portal.softcom.ua/content/pictures/news/115/0_9a04a7a25c3ffa24f9710eed855303b4.webp',
		title: 'title2',
		text: 'text',
	},
	{
		img: 'https://portal.softcom.ua/content/pictures/news/115/0_9a04a7a25c3ffa24f9710eed855303b4.webp',
		title: 'title2',
		text: 'text',
	},
	{
		img: 'https://portal.softcom.ua/content/pictures/news/115/0_9a04a7a25c3ffa24f9710eed855303b4.webp',
		title: 'title2',
		text: 'text',
	},
	{
		img: 'https://portal.softcom.ua/content/pictures/news/115/0_9a04a7a25c3ffa24f9710eed855303b4.webp',
		title: 'title2',
		text: 'text',
	},
];
const tabsTitles = ['Новини маркетингу', 'Новини компанії'];
const tabsItems = [
	<CompanyMarketingList admin={true} newsList={newsList} linkTo="marketing-separate-news" />,
	<CompanyMarketingList admin={true} newsList={newsList2} linkTo="company-separate-news" />,
];

const AdminCompanyMarketingNewsPage = () => {
	useScrollToTop();

	const [searchParams, setSearchParams] = useSearchParams();

	let [tabIndex, setTabIndex] = useState(0);

	useEffect(() => {
		let startedTab = searchParams.get('activetab');
		if (startedTab && startedTab > -1 && startedTab < tabsItems.length) setTabIndex(startedTab * 1);
	}, [searchParams]);

	return (
		<>
			{/* Mobile */}
			<div className="mobile">
				<h1>Новини маркетингу та компанії</h1>
				<div className="row">
					<div style={{ maxWidth: '580px', width: '100%' }}>
						<Tabs titleTabs={tabsTitles} setIndex={setTabIndex} tabIndex={tabIndex} />
					</div>
					{tabsItems.map((tabItem, itemIndex) => {
						if (itemIndex === tabIndex) {
							return tabItem;
						}
					})}
				</div>
			</div>

			{/* Desktop */}
			<div className="desktop">
				<div style={{ marginTop: '0' }} className="row">
					<div className="column-50">
						<h1>Новини маркетингу</h1>
						<CompanyMarketingList admin={true} newsList={newsList} linkTo="marketing-separate-news" />
					</div>
					<div className="column-50">
						<h1>Новини компанії</h1>
						<CompanyMarketingList admin={true} newsList={newsList} linkTo="company-separate-news" />
					</div>
				</div>
			</div>
		</>
	);
};

export default AdminCompanyMarketingNewsPage;
