import s from './NewsItem.module.css';

const NewsItem = ({ item }) => {
	let { title, pub_date, tags, text } = item;

	return (
		<div className={`${s.item} ${s.active}`}>
			<div className={s.left_column}>
				<p className={s.title}>{title}</p>
				<p className={s.date}>{pub_date}</p>
				<div className={s.tags}>
					{tags.map((item) => (
						<div style={item === 'День стажу' && { backgroundColor: '#5182FE' }} className={s.tag}>
							<p>{item}</p>
						</div>
					))}
				</div>
				<p style={{ webkitLineClamp: 'none !important' }} className={`${s.text} ${s.active}`}>
					{text}
				</p>
			</div>
			<div className={s.right_column}></div>
		</div>
	);
};

export default NewsItem;
