import s from './AdminPostSection.module.css';
import { useEffect, useRef, useState } from 'react';
import smileIcon from '../../../assets/img/icons/smile-icon.svg';
import EmojiList from '../../../components/EmojiList/EmojiList';

const FormTextBlock = ({ text, setText, validationErrors }) => {
	let [visibleStatus2, setVisibleStatus2] = useState(false);
	let [selectionStart, setSelectionStart] = useState(null);
	let [selectionEnd, setSelectionEnd] = useState(null);

	const editorRef = useRef(null);

	const swiftInsert = (original, index, insert) => original.substring(0, index) + insert + original.substring(index);

	// Встановлюємо початковий HTML тільки один раз
	useEffect(() => {
		if (editorRef.current && text && !editorRef.current.innerHTML) {
			editorRef.current.innerHTML = text;
		}
	}, [text]);

	const handleButtons = (e, caseValue) => {
		e.preventDefault();
		editorRef.current.focus(); // Зберігає фокус для вставки

		switch (caseValue) {
			case 'bold':
				document.execCommand('bold');
				break;
			case 'italic':
				document.execCommand('italic');
				break;
			case 'underline':
				document.execCommand('underline');
				break;
			case 'link':
				const url = prompt('Введіть посилання:');
				if (url) {
					document.execCommand('createLink', false, url);
				}
				break;
			default:
				break;
		}
		setText(editorRef.current.innerHTML); // зберігаємо HTML після команди
	};

	const handleKeyDown = (e) => {
		if (e.key === 'Enter') {
			e.preventDefault(); // Зупиняємо стандартну поведінку
			document.execCommand('insertHTML', false, '<br><br>'); // вставляємо <br>
		}
	};

	const changeTextHandler = () => {
		setText(editorRef.current.innerHTML);
	};

	return (
		<div className={s.post_item}>
			<p>
				Текст події:
				<span style={{ fontSize: '20px', color: 'red' }} title="Обов`язкове">
					*
				</span>
			</p>
			{/* <div className={s.row_btns}>
				<button onClick={boldBtnOnClickHandler}>
					<b>b</b>
				</button>
				<button onClick={boldBtnOnClickHandler}>
					<i>i</i>
				</button>
				<button onClick={boldBtnOnClickHandler}>
					<span style={{ textDecoration: 'underline' }}>u</span>
				</button>
				<button onClick={boldBtnOnClickHandler}>
					<span>link</span>
				</button>
			</div> */}
			{/* <textarea
				value={text}
				onSelect={(e) => {
					setSelectionStart(e.target.selectionStart);
					setSelectionEnd(e.target.selectionEnd);
				}}
				onChange={(e) => {
					setText(e.target.value);
				}}
				placeholder="Текст події"
				className={`${s.text} ${validationErrors.text ? s.validated : ''}`}></textarea> */}
			<div className={s.row_btns}>
				<button onClick={(e) => handleButtons(e, 'bold')}>
					<b>b</b>
				</button>
				<button onClick={(e) => handleButtons(e, 'italic')}>
					<i>i</i>
				</button>
				<button onClick={(e) => handleButtons(e, 'underline')}>
					<u>u</u>
				</button>
				<button onClick={(e) => handleButtons(e, 'link')}>link</button>
			</div>

			<div
				ref={editorRef}
				className="editor"
				contentEditable
				onInput={changeTextHandler}
				onKeyDown={handleKeyDown}
				suppressContentEditableWarning={true}></div>
			<button
				title="Смайлики"
				onClick={(e) => {
					e.preventDefault();
					setVisibleStatus2((status) => !status);
				}}
				className={s.smile_button}>
				<img src={smileIcon} alt="" />
			</button>
			<EmojiList
				visibleStatus={visibleStatus2}
				setSmile={(val) => {
					editorRef.current.innerHTML += val;
					setText(editorRef.current.innerHTML);
				}}
			/>
		</div>
	);
};

export default FormTextBlock;
