import s from './ShopProduct.module.css';

const ShopProduct = ({ productData }) => {
	let { name, cost, available, id, img } = productData;

	return (
		<>
			<Link to={`/product/${id}`} className={s.item} key={`${id}product`}>
				{galleryItem.thumb && <img className={s.main_img} src={`${galleryItem.thumb}`} />}
				<p className={s.item_title}>
					<span className={s.item_date}>
						<img src={clockIcon} alt="" /> {reversDateWithoutTime(galleryItem.create_date)}
					</span>
					{galleryItem.title}
				</p>
				<span className={s.item_btn} href="">
					Переглянути
					<img src={arrowIcon} alt="" />
				</span>
				{adminStatus && <EditAdminButton link={`add-change-gallery/${galleryItem.id}`} />}
			</Link>
		</>
	);
};

export default ShopProduct;
