 * Licensed under the MIT License.
 */

import React from 'react';
import Navbar from 'react-bootstrap/Navbar';

import { useIsAuthenticated } from '@azure/msal-react';
import { SignInButton } from './SignInButton/SignInButton';
import { SignOutButton } from './SingOutButton/SignOutButton';

/**
 * Renders the navbar component with a sign-in or sign-out button depending on whether or not a user is authenticated
 * @param props
 */
export const PageLayout = (props) => {
	const isAuthenticated = useIsAuthenticated();

	return (
		<>
			<Navbar bg="primary" variant="dark" className="navbarStyle">
				<a className="navbar-brand" href="/">
					Microsoft Identity Platform
				</a>
				<div className="collapse navbar-collapse justify-content-end">{isAuthenticated ? <SignOutButton /> : <SignInButton />}</div>
			</Navbar>

			<div className="profileContent">{props.children}</div>
		</>
	);
};
