import { useEffect, useState } from 'react';
import ModalWidnow from '../UI/ModalWidnow/ModalWidnow';
import Loader from '../UI/Loader/Loader';
import s from './UserInfoModalWindow.module.css';
import { getUserById } from '../../api/graph';
import { useMsal } from '@azure/msal-react';

const UserInfoModalWindow = ({ userId, visibleStatus, closeModal }) => {
	const { instance, accounts } = useMsal();
	let [userData, setUserData] = useState(false);
	let [isFetching, setIsFetching] = useState(false);

	function RequestUsersData() {
		setIsFetching(true);
		instance
			.acquireTokenSilent({
				scopes: ['User.Read.All'],
				account: accounts[0],
			})
			.then((response) => {
				getUserById(response.accessToken, userId)
					.then((response) => response.json())
					.then((result) => {
						setIsFetching(false);
						setUserData(result);
						console.log(result);
					})
					.catch((rerror) => {
						setIsFetching(false);
					});
			});

		setTimeout(() => {
			setIsFetching(false);
		}, 12000);
	}

	useEffect(() => {
		console.log(userId);
		RequestUsersData();
	}, [userId]);

	return (
		<>
			{visibleStatus && (
				<ModalWidnow closeModal={closeModal}>
					{isFetching ? (
						<Loader />
					) : (
						<>
							{userData && (
								<div className={s.info_container}>
									<div className={s.user_row}>
										<div>
											<p>{userData.displayName}</p>
											<p>{userData.jobTitle}</p>
										</div>
									</div>
								</div>
							)}
						</>
					)}
				</ModalWidnow>
			)}
		</>
	);
};

export default UserInfoModalWindow;
