import s from './TodaySection.module.css';
import autumn from '../../../assets/img/osin.jpg';
import { useEffect, useState } from 'react';
import { dayConverter, weekDayConverter, monthFullConverter } from '../../../helpers/dateConverter';

const TodaySection = () => {
	let { date, setDate } = useState({ day: '', month: '', dayWeekk: '' });

	useEffect(() => {
		let date = new Date();
		let day = date.getDate();
		let month = date.getMonth() + 1;
		let dayWeekk = date.getDay();

		setDate({ ...date, day: '', month: '', dayWeekk: '' });

		console.log(dayConverter(day), monthFullConverter(month), weekDayConverter(dayWeekk));
	});

	return (
		<div className={`${s.today_section} section-container`}>
			<img className={s.bg_img} src={autumn} alt="" />

			<div className={s.today_content}>
				<p>Сьогодні</p>
				<h2>
					02 <span>пн.</span>
					<br />
					Жовтня
				</h2>
				<p>Актуальне нижче</p>
			</div>
		</div>
	);
};

export default TodaySection;
