import s from './BirthdaySection.module.css';
import searchIcon from '../../../assets/img/icons/search-icon.svg';
import { getAllUsers, getPhotoUser } from '../../../api/graph';
import { useMsal } from '@azure/msal-react';
import { loginRequest } from '../../../authConfig';
import { useEffect, useState } from 'react';

const BirthdaySection = () => {
	const { instance, accounts } = useMsal();

	let [users, setUsers] = useState();
	let [nulledUsers, setNulledUsers] = useState();
	let [visibleUsers, setVisibleUsers] = useState();

	function RequestUsersData() {
		instance
			.acquireTokenSilent({
				...loginRequest,
				account: accounts[0],
			})
			.then((response) => {
				getAllUsers(response.accessToken)
					.then((response) => response.json())
					.then((result) => {
						let filterResult = result.value.filter(
							(user) =>
								user.accountEnabled &&
								user.onPremisesExtensionAttributes.extensionAttribute2 &&
								user.onPremisesExtensionAttributes.extensionAttribute2 != 'День народження'
						);

						let year = new Date().getFullYear();
						let nulledDay = new Date(`${year}-01-01`);

						const dateToYMD = (date) => {
							let d = date.getDate();
							let m = date.getMonth() + 1;
							return new Date('' + year + '-' + (m <= 9 ? '0' + m : m) + '-' + (d <= 9 ? '0' + d : d));
						};

						const dateToYMDArray = (date) => {
							let d = date.slice(0, 2);
							let m = date.slice(3, 5);

							return new Date('' + year + '-' + m + '-' + d);
						};

						let today = new Date(); // сегодня

						let filteredArray = filterResult.map((item) => {
							// днів до сьогодні
							let daysToday = Math.ceil(Math.abs(dateToYMD(today).getTime() - nulledDay.getTime()) / (1000 * 3600 * 24));
							let allDaysInYear = Math.ceil(Math.abs(new Date(`${year}-12-31`).getTime() - nulledDay.getTime()) / (1000 * 3600 * 24));
							let daysA = Math.ceil(
								Math.abs(dateToYMDArray(item.onPremisesExtensionAttributes.extensionAttribute2).getTime() - nulledDay.getTime()) /
									(1000 * 3600 * 24)
							);

							if (daysA - daysToday < 0) {
								return { ...item, index: allDaysInYear + 1 + daysA - daysToday };
							}
							return { ...item, index: daysA - daysToday };
						});

						filteredArray.sort((a, b) => a.index - b.index);

						setUsers(filteredArray);
					});
			});
	}
	function RequestProfilePhoto(id, index) {
		instance
			.acquireTokenSilent({
				...loginRequest,
				account: accounts[0],
			})
			.then((response) => {
				getPhotoUser(response.accessToken, id)
					.then((response) => response.blob())
					.then((result) => {
						const url = window.URL || window.webkitURL;
						const blobUrl = url.createObjectURL(result);

						setVisibleUsers((value) => {
							let arr = [...value];

							arr[index].urlImg = blobUrl;
							return arr;
						});
					});
			});
	}
	function RequestProfilePhoto1(id) {
		return instance
			.acquireTokenSilent({
				...loginRequest,
				account: accounts[0],
			})
			.then((response) => {
				return getPhotoUser(response.accessToken, id)
					.then((response) => response.blob())
					.then((result) => {
						const url = window.URL || window.webkitURL;
						const blobUrl = url.createObjectURL(result);

						return blobUrl;
					});
			});
	}

	useEffect(() => {
		RequestUsersData();
	}, []);

	useEffect(() => {
		if (users) {
			let test = async () => {
				let data = await Promise.all(
					users.slice(0, 4).map(async (item) => {
						let imgUrl = await RequestProfilePhoto1(item.id);

						return { ...item, urlImg: imgUrl };
					})
				);

				setVisibleUsers(data);
				setNulledUsers(data);
			};
			test();
		}
	}, [users]);

	return (
		<div className={s.birthday}>
			<h3>Майбутні дні народження</h3>

			<div className={s.birthday_search}>
				<button>
					<img src={searchIcon} alt="bell icon" />
				</button>
				<input type="text" placeholder="Пошук тут..." />
			</div>

			<ul className={s.birthday_list}>
				{visibleUsers &&
					visibleUsers.map((user) => (
						<li className={s.birthday_item}>
							<div className={s.birthday_item__user}>
								<img src={user.urlImg && user.urlImg} alt="user" />
								<div>
									<p>{user.displayName}</p>
									<p>{user.jobTitle}</p>
								</div>
							</div>
							<p className={s.birthday_item__date}>{user.onPremisesExtensionAttributes.extensionAttribute2}</p>
							<div className={s.birthday_item__buttons}>
								<button>1</button>
								<button>2</button>
							</div>
						</li>
					))}
			</ul>
		</div>
	);
};

export default BirthdaySection;
