import React, { useState } from 'react';
import UserInfoModalWindow from '../UserInfoModalWindow/UserInfoModalWindow';

const UserInfoContainer = ({ children, id }) => {
	let [visibleStatus, setVisibleStatus] = useState(false);

	const visibleButtonOnClickHandler = () => {
		setVisibleStatus((status) => !status);
	};

	return (
		<>
			<button style={{ backgroundColor: 'transparent' }} onClick={visibleButtonOnClickHandler}>
				{children}
			</button>
			{visibleStatus && <UserInfoModalWindow closeModal={visibleButtonOnClickHandler} visibleStatus={visibleStatus} userId={id} />}
		</>
	);
};

export default UserInfoContainer;
