import { useEffect, useState } from 'react';
import s from './Header.module.css';
import { useMsal } from '@azure/msal-react';
import { callMsGraph, callPhoto } from '../../graph';
import { loginRequest } from '../../authConfig';

const Header = () => {
	const { instance, accounts } = useMsal();
	let [urlImg, setUrlImg] = useState(null);

	function RequestProfileData() {
		// Silently acquires an access token which is then attached to a request for MS Graph data
		instance
			.acquireTokenSilent({
				...loginRequest,
				account: accounts[0],
			})
			.then((response) => {
				callPhoto(response.accessToken)
					.then((response) => response.text())
					.then((result) => {
						setUrlImg(result);
						console.log(result);
					});
			});
	}
	function RequestProfileData2() {
		// Silently acquires an access token which is then attached to a request for MS Graph data
		instance
			.acquireTokenSilent({
				...loginRequest,
				account: accounts[0],
			})
			.then((response) => {
				callMsGraph(response.accessToken).then((response) => console.log(response));
			});
	}

	useEffect(() => {
		RequestProfileData();
		// RequestProfileData2();
	}, []);

	return (
		<header>
			<div className={`content-container ${s.header} `}>
				<div>hi</div>
				<div>2</div>
				{urlImg && <img src={`${'data:image/jpeg;base64,' + urlImg}`} alt="" />}
			</div>
		</header>
	);
};

export default Header;
