import { useEffect, useState } from 'react';
import s from './Header.module.css';
import { useMsal } from '@azure/msal-react';
import { callMsGraph, callPhoto } from '../../graph';
import { loginRequest } from '../../authConfig';

const Header = () => {
	const { instance, accounts } = useMsal();
	let [urlImg, setUrlImg] = useState(null);

	function RequestProfileData() {
		// Silently acquires an access token which is then attached to a request for MS Graph data
		instance
			.acquireTokenSilent({
				...loginRequest,
				account: accounts[0],
			})
			.then((response) => {
				callPhoto(response.accessToken).then((response) => {
					const url = window.URL || window.webkitURL;
					const blobUrl = url.createObjectURL(response);
					document.querySelector('.image').setAttribute('src', blobUrl);
				});
			});
	}
	function RequestProfileData2() {
		// Silently acquires an access token which is then attached to a request for MS Graph data
		instance
			.acquireTokenSilent({
				...loginRequest,
				account: accounts[0],
			})
			.then((response) => {
				callMsGraph(response.accessToken).then((response) => console.log(response));
			});
	}

	useEffect(() => {
		RequestProfileData();
		// RequestProfileData2();
	});

	return (
		<header>
			<div className={`content-container ${s.header} `}>
				<div>hi</div>
				<div>2</div>
				{urlImg && <img className="image" src={URL.createObjectURL(urlImg)} alt="" />}
			</div>
		</header>
	);
};

export default Header;
