import s from './AdminPostSection.module.css';
import { useState } from 'react';
import smileIcon from '../../../assets/img/icons/smile-icon.svg';
import EmojiList from '../../../components/EmojiList/EmojiList';

const FormTextBlock = ({ text, setText, validationErrors }) => {
	let [visibleStatus2, setVisibleStatus2] = useState(false);
	let [selectionStart, setSelectionStart] = useState(null);
	let [selectionEnd, setSelectionEnd] = useState(null);

	const swiftInsert = (original, index, insert) => original.substring(0, index) + insert + original.substring(index);

	const boldBtnOnClickHandler = (e) => {
		e.preventDefault();

		let tagStart = '';
		let tagEnd = '';
		let target = e.target.innerHTML;

		console.log(target);

		if (target === 'b') {
			tagStart = '<b>';
			tagEnd = '</b>';
		}
		if (target === 'i') {
			tagStart = '<i>';
			tagEnd = '</i>';
		}
		if (target === 'u') {
			tagStart = '<span style="text-decoration:underline">';
			tagEnd = '</span>';
		}
		if (target === 'link') {
			tagStart = '<a href="ПОСИЛАННЯ">';
			tagEnd = '</a>';
		}

		// if (text.length > 0) {
		// 	let selection = getSelection();
		// 	console.log(selection);

		// 	let X = getSelection().getRangeAt(0).endOffset;
		// 	let Y = getSelection().getRangeAt(0).startOffset;
		// 	console.log(X);
		// 	console.log(Y);
		// }

		console.log(text);
		console.log(text.toString());
		console.log(text.indexOf('\n'));
		console.log(selectionStart);
		console.log(selectionEnd);

		setText((currentText) => {
			let firstSubtext = swiftInsert(currentText, selectionStart, tagStart);
			console.log(swiftInsert(firstSubtext, selectionEnd + tagStart.length, tagEnd));
			return swiftInsert(firstSubtext, selectionEnd + tagStart.length, tagEnd);
		});
	};

	return (
		<div className={s.post_item}>
			<p>
				Текст події:
				<span style={{ fontSize: '20px', color: 'red' }} title="Обов`язкове">
					*
				</span>
			</p>
			<div className={s.row_btns}>
				<button onClick={boldBtnOnClickHandler}>
					<b>b</b>
				</button>
				<button onClick={boldBtnOnClickHandler}>
					<i>i</i>
				</button>
				<button onClick={boldBtnOnClickHandler}>
					<span style={{ textDecoration: 'underline' }}>u</span>
				</button>
				<button onClick={boldBtnOnClickHandler}>
					<span>link</span>
				</button>
			</div>
			<textarea
				value={text}
				onSelect={(e) => {
					setSelectionStart(e.target.selectionStart);
					setSelectionEnd(e.target.selectionEnd);
				}}
				onChange={(e) => {
					console.log(text);
					let convertedText2 = e.target.innerHTML.replace(/\n/g, '<br>');
					console.log(convertedText2);
					console.log(convertedText2.replace(/<br>/g, '\n'));
					setText(e.target.value);
				}}
				placeholder="Текст події"
				className={`${s.text} ${validationErrors.text ? s.validated : ''}`}></textarea>
			<button
				title="Смайлики"
				onClick={(e) => {
					e.preventDefault();
					setVisibleStatus2((status) => !status);
				}}
				className={s.smile_button}>
				<img src={smileIcon} alt="" />
			</button>
			<EmojiList visibleStatus={visibleStatus2} setSmile={setText} />
		</div>
	);
};

export default FormTextBlock;
