import s from './AlertModalWindow.module.css';
import Loader from '../UI/Loader/Loader';
import { Link } from 'react-router-dom';

const AlertModalWindow = ({ data }) => {
	let { acceptButton, cencelButton, isFetching, fetchingResult, text } = data;
	return (
		<div className={s.modal}>
			<div className={s.modal_container}>
				{isFetching ? (
					<Loader />
				) : (
					<div className={s.content}>
						<p className={s.text}>{!fetchingResult && text.question}</p>
						<div className={s.buttons}>
							<Link to={cencelButton.path}>{cencelButton.text}</Link>
							<button onClick={acceptButton.handler}>{acceptButton.text}</button>
						</div>
					</div>
				)}
			</div>
		</div>
	);
};

export default AlertModalWindow;
