import { useEffect, useState } from 'react';
import { useParams, useSearchParams } from 'react-router-dom';
// import AdminHolidaysFormSection from './AdminHolidaysFormSection/AdminHolidaysFormSection';
import { getNewsFromID } from '../../api/api';
import { useRedirectAdmin } from '../../hooks/useRedirectHoook';
import { useScrollToTop } from '../../hooks/scrollToTop';
import GoBackButton from '../../components/UI/GoBackButton/GoBackButton';

const AdminAddChangeCompanyMarketingNewsPage = () => {
	let { statusAdmin } = useRedirectAdmin();
	let { newsId } = useParams();
	let [data, setData] = useState({});
	let [type, setType] = useState(false);

	const [searchParams, setSearchParams] = useSearchParams();

	useScrollToTop();

	useEffect(() => {
		if (newsId && statusAdmin) {
			let getNews = async () => {
				let result = await getNewsFromID(newsId);
				setData(result[0]);
			};
			getNews();
		}
	}, [newsId, statusAdmin]);

	useEffect(() => {
		let typeParams = searchParams.get('type');
		if (typeParams) setType(typeParams);
	}, [searchParams]);

	return (
		<>
			{statusAdmin && (
				<div>
					{newsId ? (
						<h1>Редагування новини {type && type === 'marketing' ? 'маркетингу' : 'компанії'}</h1>
					) : (
						<h1>Створення новини {type && type === 'marketing' ? 'маркетингу' : 'компанії'}</h1>
					)}

					<GoBackButton toLink={'/admin-company-marketing-news'} />

					<div className="row">
						<div className="column-50">{/* <AdminHolidaysFormSection Id={Id} data={data} /> */}</div>
					</div>
				</div>
			)}
		</>
	);
};

export default AdminAddChangeCompanyMarketingNewsPage;
