import s from './BirthdaySection.module.css';
import searchIcon from '../../../assets/img/icons/search-icon.svg';
import { getAllUsers } from '../../../api/graph';
import { useMsal } from '@azure/msal-react';
import { loginRequest } from '../../../authConfig';
import { useEffect } from 'react';

const BirthdaySection = () => {
	const { instance, accounts } = useMsal();

	function RequestUsersData() {
		instance
			.acquireTokenSilent({
				...loginRequest,
				account: accounts[0],
			})
			.then((response) => {
				getAllUsers(response.accessToken)
					.then((response) => response.json())
					.then((result) => {
						let filterResult = result.value.filter((user) => user.accountEnabled && user.onPremisesExtensionAttributes.extensionAttribute2);

						// let now = new Date();

						const dateToYMD = (date) => {
							let d = date.getDate();
							let m = date.getMonth() + 1;

							return '' + (m <= 9 ? '0' + m : m) + '-' + (d <= 9 ? '0' + d : d);
						};
						const dateToYMDArray = (date) => {
							let d = date.slice(0, 2);
							let m = date.slice(2, 5);

							return '' + (m <= 9 ? '0' + m : m) + '-' + (d <= 9 ? '0' + d : d);
						};
						// dateToYMD(new Date(now));

						const dt = new Date();
						const now = +[dt.getMonth() + 1, dt.getDate()].map((v) => `0${v}`.slice(-2)).join('');
						console.log(dateToYMD(new Date()));
						console.log(dateToYMDArray(filterResult[0].onPremisesExtensionAttributes.extensionAttribute2));

						// filterResult.sort((a, b) => {
						// 	let currentA = dateToYMDArray(a.onPremisesExtensionAttributes.extensionAttribute2);
						// 	let currentB = dateToYMDArray(b.onPremisesExtensionAttributes.extensionAttribute2);
						// 	return Math.abs(+currentA.replace(/[^\d]+/, '') - now) - Math.abs(+currentB.replace(/[^\d]+/, '') - now);
						// });

						console.log(filterResult.map((i) => i.onPremisesExtensionAttributes.extensionAttribute2));

						// console.log(result);
						// console.log(filterResult);
					});
			});
	}

	useEffect(() => {
		RequestUsersData();
	}, []);

	return (
		<div className={s.birthday}>
			<h3>Майбутні дні народження</h3>

			<div className={s.birthday_search}>
				<button>
					<img src={searchIcon} alt="bell icon" />
				</button>
				<input type="text" placeholder="Пошук тут..." />
			</div>

			<ul className={s.birthday_list}>
				<li className={s.birthday_item}>
					<div className={s.birthday_item__user}>
						<img src="" alt="user" />
						<div>
							<p>Name</p>
							<p>Job Title</p>
						</div>
					</div>
					<p className={s.birthday_item__date}>27 жовтня</p>
					<div className={s.birthday_item__buttons}>
						<button>1</button>
						<button>2</button>
					</div>
				</li>
			</ul>
		</div>
	);
};

export default BirthdaySection;
