export const dateConverter = (date) => {
	let day = date.slice(0, 2);
	let month = date.slice(3, 5) * 1;

	const uaMonth = ['січ.', 'лют.', 'берез.', 'квіт.', 'трав.', 'черв.', 'лип.', 'серп.', 'верес.', 'жовт.', 'листоп.', 'груд.'];

	return `${day} ${uaMonth[month - 1]}`;
};

let hiddenItems = () => {
	let currentList = document.querySelector('.display_list.unshow_un_props');
	let currentItems = currentList.querySelectorAll('.list_item_wrapp.item_wrap');

	currentItems.forEach((item) => {
		let value = item.querySelector('.value');
		let currentButton = item.querySelector('.button_block');

		if (value === 'Немає в наявності') {
			currentButton.style.display = 'none';
		}
	});
};
