export const dateConverter = (date) => {
	let day = date.slice(0, 2);
	let month = date.slice(3, 5) * 1;

	const uaMonth = [
		'січня',
		'лютого',
		'березня',
		'квітня',
		'травня',
		'червня',
		'липня',
		'серпня',
		'вересня',
		'жовтня',
		'листопада',
		'грудня',
	];

	switch (month) {
		case 1:
			return uaMonth[month - 1];

		default:
			break;
	}

	return month;
};
