const days = ['нд.', 'пн.', 'вт.', 'ср.', 'чт.', 'пт.', 'сб.'];
const uaMonths = [
	'січня',
	'лютого',
	'березня',
	'квітня',
	'травня',
	'червня',
	'липня',
	'серпня',
	'вересня',
	'жовтня',
	'листопада',
	'грудня',
];
const uaMonthsSliced = ['січ.', 'лют.', 'берез.', 'квіт.', 'трав.', 'черв.', 'лип.', 'серп.', 'верес.', 'жовт.', 'листоп.', 'груд.'];

export const dateConverter = (date) => {
	let day = date.slice(0, 2);
	let month = date.slice(3, 5) * 1;

	return `${day} ${uaMonthsSliced[month - 1]}`;
};
export const monthFullConverter = (date) => {
	return days[date - 1];
};
export const weekDayConverter = (date) => {
	return days[date];
};
