];

const ShopListSection = () => {
	let [isFetched, setFetched] = useState(false);

	return (
		<div className="row">
			{isFetched ? (
				<div style={{ width: '100%', height: '100px', position: 'relative', marginTop: '30px' }}>
					<Loader background={true} />
				</div>
			) : (
				<ShopList products={data} />
			)}
		</div>
	);
};

export default ShopListSection;
