import { useEffect, useState } from 'react';
import s from './FilesList.module.css';

const FilesList = ({ data }) => {
	let [isFiles, setFiles] = useState([]);

	useEffect(() => {
		data && setFiles(data);
	}, [data]);

	return (
		<div className="row">
			<div className={s.files}>
				{isFiles.length > 0 &&
					isFiles.map(
						(fileItem) =>
							fileItem && (
								<div className={s.files__item}>
									{fileItem.file_type === 'image' && <img loading="lazy" className={s.file} src={fileItem.url} alt="" />}
									{file.file_type === 'video' && <video controls loading="lazy" className={s.file} src={`${file.url}`} alt="" />}
									{file.file_type !== 'video' && file.file_type !== 'image' && (
										<div
											style={{
												width: '100%',
												height: '100%',
												display: 'flex',
												flexDirection: 'column',
												alignItems: 'center',
												justifyContent: 'center',
												gap: '16px',
												position: 'absolute',
												top: 0,
												left: 0,
												padding: '20px',
											}}>
											<span style={{ textAlign: 'center', overflowWrap: 'break-word', width: '100%' }}>{file.file_type}</span>
											<span style={{ textAlign: 'center', overflowWrap: 'break-word', width: '100%' }}>{file.url}</span>
										</div>
									)}
								</div>
							)
					)}
			</div>
		</div>
	);
};

export default FilesList;
