import { useEffect, useState } from 'react';
import s from './ShopProductCard.module.css';
import ShopProductDescription from './ShopProductDescription/ShopProductDescription';
import ShopProductImages from './ShopProductImages/ShopProductImages';
import ShopProductParams from './ShopProductParams/ShopProductParams';

export const ShopProductCard = ({ productData }) => {
	let [isProductData, setProductData] = useState({
		title: 'Футболка поло',
		cost: '500',
		available: true,
		id: 100,
		images: ['https://portal.softcom.ua/content/pictures/2025/5/12/0_4ce5d999f8f8779e69cde7ebd4d679e3.webp'],
		description: 'text 123 123 123 123 lorem',
	});
	let params = { ...isProductData.title, ...isProductData.cost, ...isProductData.available, ...isProductData.id };

	useEffect(() => {
		if (productData) {
			setProductData(productData);
		}
	}, [productData]);

	return (
		<div className="row">
			<div className={s.product}>
				<div className={s.product_row}>
					<ShopProductImages images={isProductData.images} />
					<ShopProductParams params={params} />
				</div>
				<ShopProductDescription description={params.description} />
			</div>
		</div>
	);
};
