import { useState } from 'react';
import ShopList from '../../components/ShopList/ShopList';
import Loader from '../../components/UI/Loader/Loader';

const data = [
	{
		name: 'Футболка поло',
		cost: '500',
		available: true,
		id: 100,
	},
	{
		name: 'Футболка поло',
		cost: '500',
		available: false,
		id: 100,
	},
	{
		name: 'Футболка поло',
		cost: '500',
		available: true,
	},
	{
		name: 'Футболка поло',
