import s from './AlertModalWindow.module.css';
import Loader from '../UI/Loader/Loader';

const AlertModalWindow = ({ data }) => {
	let { acceptButton, cencelButton, isFetching, fetchingResult, text } = data;
	return (
		<div className={s.modal}>
			<div className={s.modal_container}>{isFetching ? <Loader /> : ''}</div>
		</div>
	);
};

export default AlertModalWindow;
