import { useParams } from 'react-router-dom';
import s from './FeedSeparateNewsPage.module.css';
import { useState } from 'react';
import { getNewsFromID } from '../../api/api';
import { useScrollToTop } from '../../hooks/scrollToTop';
import NewsItem from '../../components/NewsItem/NewsItem';

const FeedSeparateNewsPage = () => {
	let { newsId } = useParams();
	let [data, setData] = useState(null);

	useScrollToTop();

	useEffect(() => {
		if (newsId) {
			let getNews = async () => {
				let result = await getNewsFromID(newsId);
				setData(result[0]);
			};
			getNews();
		}
	}, [newsId]);

	return (
		<div>
			<h1>Стрічка останніх подій</h1>

			{data ? <NewsItem item={data} filterParams={true} adminStatus={false} fullScreen={true} /> : <p>Подію не знайдено</p>}
		</div>
	);
};

export default FeedSeparateNewsPage;
