import s from './BirthdaySection.module.css';
import searchIcon from '../../../assets/img/icons/search-icon.svg';
import { getAllUsers } from '../../../api/graph';
import { useMsal } from '@azure/msal-react';
import { loginRequest } from '../../../authConfig';
import { useEffect } from 'react';

const BirthdaySection = () => {
	const { instance, accounts } = useMsal();

	function RequestUsersData() {
		instance
			.acquireTokenSilent({
				...loginRequest,
				account: accounts[0],
			})
			.then((response) => {
				getAllUsers(response.accessToken)
					.then((response) => response.json())
					.then((result) => {
						let filterResult = result.value.filter((user) => user.accountEnabled && user.onPremisesExtensionAttributes.extensionAttribute2);
						// Создание даты
						let date = Date.now(); // 1537043086974
						// Работа с датой
						let formatedDate = Intl.DateTimeFormat('ru').format(date); // "15.09.2018"
						console.log(date);
						console.log(formatedDate);

						console.log(result);
						console.log(filterResult);
					});
			});
	}

	useEffect(() => {
		RequestUsersData();
	}, []);

	return (
		<div className={s.birthday}>
			<h3>Майбутні дні народження</h3>

			<div className={s.birthday_search}>
				<button>
					<img src={searchIcon} alt="bell icon" />
				</button>
				<input type="text" placeholder="Пошук тут..." />
			</div>

			<ul className={s.birthday_list}>
				<li className={s.birthday_item}>
					<div className={s.birthday_item__user}>
						<img src="" alt="user" />
						<div>
							<p>Name</p>
							<p>Job Title</p>
						</div>
					</div>
					<p className={s.birthday_item__date}>27 жовтня</p>
					<div className={s.birthday_item__buttons}>
						<button>1</button>
						<button>2</button>
					</div>
				</li>
			</ul>
		</div>
	);
};

export default BirthdaySection;
