import s from './NewsImgSlider.module.css';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import 'swiper/css/navigation';
import { Navigation } from 'swiper/modules';
import { useRef } from 'react';

const NewsImgSlider = ({ img }) => {
	const navigationNextRef = useRef(null);
	const navigationPrevRef = useRef(null);

	return (
		<div className={s.slider}>
			<Swiper
				modules={[Navigation]}
				navigation={{
					prevEl: navigationPrevRef.current,
					nextEl: navigationNextRef.current,
				}}
				onBeforeInit={(swiper) => {
					swiper.navigation.nextEl = navigationNextRef.current;
					swiper.navigation.prevEl = navigationPrevRef.current;
				}}
				spaceBetween={50}
				slidesPerView={1}
				onSlideChange={() => console.log('slide change')}
				onSwiper={(swiper) => console.log(swiper)}>
				{img &&
					img.map((item) => (
						<SwiperSlide>
							<div className={s.slider_slide}>
								<div className={s.slider_img_container}>
									<img src={item} alt="" />
								</div>
							</div>
						</SwiperSlide>
					))}
			</Swiper>
			<div>
				<button ref={navigationNextRef}>Next</button>
				<button ref={navigationPrevRef}>Prev</button>
			</div>
		</div>
	);
};

export default NewsImgSlider;
