import React, { useEffect, useState } from 'react';
import { getGalleryCatalogsByID } from '../../api/api';
import { useParams } from 'react-router-dom';
import { useScrollToTop } from '../../hooks/scrollToTop';
import GoBackButton from '../../components/UI/GoBackButton/GoBackButton';
import FilesList from '../../components/FilesList/FilesList';

const GallerySeparatePage = () => {
	useScrollToTop();

	let { Id } = useParams();
	let [data, setData] = useState({});

	useEffect(() => {
		Id && getGalleryCatalogsByID(Id).then((response) => setData(...response));
	}, [Id]);

	return (
		<div>
			<h1>{data.title}</h1>
			<GoBackButton toLink="/gallery" />
			<div className="row">
				<p>filter bar</p>
				<FilesList data={data.files} />
				<p>modal window</p>
			</div>
		</div>
	);
};

export default GallerySeparatePage;
