import { useEffect, useState } from 'react';
import s from './NewsFilter.module.css';

const NewsFilter = ({ tags }) => {
	let [activeButton, setActiveButton] = useState(0);

	useEffect(() => {
		console.log(tags);
	}, [tags]);

	return (
		<div className={s.filters}>
			<div className={`${s.filters_row} ${s.params}`}>
				<button className={activeButton === 0 && s.active}>Усі</button>
				<button className={activeButton && s.active}>Сьогодні</button>
				<button className={activeButton && s.active}>Популярне</button>
			</div>
			<div className={`${s.filters_row} ${s.tags}`}>
				<button>День стажу</button>
				<button>День народження</button>
			</div>
		</div>
	);
};

export default NewsFilter;
