import { useEffect } from 'react';
import ModalWidnow from '../UI/ModalWidnow/ModalWidnow';
import s from './UserInfoModalWindow.module.css';
import { getUserById } from '../../api/graph';
import { useMsal } from '@azure/msal-react';

const UserInfoModalWindow = ({ userId, visibleStatus, closeModal }) => {
	const { instance, accounts } = useMsal();

	useEffect(() => {
		console.log(userId);

		function RequestUsersData() {
			instance
				.acquireTokenSilent({
					scopes: ['User.Read.All'],
					account: accounts[0],
				})
				.then((response) => {
					getUserById(response.accessToken, userId)
						.then((response) => response.json())
						.then((result) => {
							console.log(result);
						});
				});
		}
		RequestUsersData();
	}, [userId]);

	return (
		<>
			{visibleStatus && (
				<ModalWidnow closeModal={closeModal}>
					<div className={s.info_container}></div>
				</ModalWidnow>
			)}
		</>
	);
};

export default UserInfoModalWindow;
