import React from 'react';

const FilesFilter = ({ changeFilter }) => {
	return (
		<div className="row">
			<button onClick={() => changeFilter(null)}>Усі</button>
			<button onClick={() => changeFilter('image')}>Зображення</button>
			<button onClick={() => changeFilter('video')}>Відео</button>
		</div>
	);
};

export default FilesFilter;
