import s from './NotificationModule.module.css';
import bellIcon from '../../assets/img/icons/bell-icon.svg';
import { useState } from 'react';

const NotificationModule = () => {
	let [isOpenedStatus, setOpenedStatus] = useState(false);

	return (
		<div className={`${s.notification_container} ${isOpenedStatus && s.active}`}>
			<button onClick={() => setOpenedStatus((status) => !status)} title="Сповіщення" className={s.notification_btn}>
				<img src={bellIcon} alt="bell icon" />
			</button>

			<div className={s.notification_body}></div>
		</div>
	);
};

export default NotificationModule;
